using System;  namespace Hello_World {     public class Program     {         public static void Main(string[] args)         {                       }         public static bool IsSorted(int[] grades, bool asc)         {             int LastGrade = grades[0];             foreach (int grade in grades)             {                 if (asc)                 {                     if (grade < LastGrade)                     {                         return false;                     }                     else                     {                         LastGrade = grade;                     }                 }                  else                 {                     if (grade > LastGrade)                     {                         return false;                     }                     else                     {                         LastGrade = grade;                     }                 }             }             return true;          }      } } 